import '@expo/metro-runtime';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, ImageBackground, Button } from 'react-native';
import React, { useState } from 'react';

export default function App() {
  const [isWelcomeScreen, setIsWelcomeScreen] = useState(true);

  const handlePress = () => {
    setIsWelcomeScreen(false);
  };

  return (
    <View style={styles.container}>
      {isWelcomeScreen ? (
        <ImageBackground source={{uri: 'https://i.imgur.com/MtHeYrE.jpg'}} style={styles.backgroundImage} resizeMode="cover">
          <View style={styles.overlay}>
            <Text style={styles.welcomeText}>塔塔大作戰</Text>
            <Button title="進入" onPress={handlePress} color="#841584" />
          </View>
        </ImageBackground>
      ) : (
        <View>
          <Image source={{uri: 'https://i.imgur.com/MtHeYrE.jpg'}} style={styles.image} />
        </View>
      )}
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  backgroundImage: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  overlay: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  welcomeText: {
    fontSize: 30,
    fontWeight: 'bold',
    marginBottom: 20,
    color: '#fff',
    textShadowColor: '#000',
    textShadowOffset: { width: 1, height: 1 },
    textShadowRadius: 10,
  },
  image: {
    width: '100%',
    height: '100%',
  },
});
