import React from 'react';
import { StyleSheet, Text, View, ImageBackground, Button } from 'react-native';

export default function WelcomeScreen({ onPress }) {
  return (
    <ImageBackground source={{uri: 'https://i.imgur.com/MtHeYrE.jpg'}} style={styles.backgroundImage} resizeMode="cover">
      <View style={styles.overlay}>
        <Text style={styles.welcomeText}>塔塔大作戰</Text>
        <Button title="進入" onPress={onPress} color="#841584" />
      </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  backgroundImage: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  overlay: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  welcomeText: {
    fontSize: 30,
    fontWeight: 'bold',
    marginBottom: 20,
    color: '#fff',
    textShadowColor: '#000',
    textShadowOffset: { width: 1, height: 1 },
    textShadowRadius: 10,
  },
});
